/* -------------------------- SignUp Validation -------------------------- */

function signupValidate() {
  var name = document.log.name.value;
  var email = document.log.email.value;
  var pwd = document.log.pwd.value;
  var cnfrmpwd = document.log.cnfrmpwd.value;
  console.log(name);
  console.log(email);
  console.log(pwd);
  console.log(cnfrmpwd);
  var atposition = email.indexOf("@");
  var dotposition = email.lastIndexOf(".");
  if (
    email == "" ||
    atposition < 1 ||
    dotposition < atposition + 2 ||
    dotposition + 2 >= email.length
  ) {
    document.log.email.value = "";
    document.log.email.focus();
    alert("Valid Email Is Required");
    return false;
  }
  if (!isNaN(name)) {
    alert("Name Is Invalid");
    document.log.name.value = "";
    document.log.name.focus();
    return false;
  }

  if (pwd != cnfrmpwd) {
    alert("passwords Does Not Match");
    document.log.cnfrm.value = "";
    document.log.cnfrm.focus();
    return false;
  }
  alert("Valid Credentials");
  return true;
}
